package com.yan.routerimport com.alibaba.android.arouter.facade.template.IProviderimport com.alibaba.android.arouter.launcher.ARouterimport kotlin.reflect.KClass/** * @author Bevan (Contact me: https://github.com/genius158) * @since  2020/3/26 */fun <T : IProvider> getRouter(clazz: Class<T>): T? {  return ARouter.getInstance()      .navigation(clazz)}fun <T : IProvider> getRouter(kClass: KClass<T>): T? = getRouter(kClass.java)